import styled from "styled-components";

export const Container = styled("div")<{ color:string}>`
  display: flex;
  background-color: ${(props) => props.color};
  border-radius: 20px;
  width: 80vw;
  height: 5vh;
  flex-direction:column;
  padding:10px 0px;
`;

export const Text = styled("p")`
  font-size: 16px;
  display:block;
`
